var limit = 60;
if(localStorage.getItem("ch4-counter")){
	if(localStorage.getItem("ch4-counter") == "Stop"){
		var value = limit;
		loadHint_Login();
	}else{
		var value = localStorage.getItem("ch4-counter");
	}
	}else{
		var value = 0;
	}
//document.getElementById('demo').innerHTML = value;
var timesRun = 0;
var counter = function (){
if(timesRun > limit){
    clearInterval(interval);
    loadHint_Login();
}
timesRun += 1;
if(value != NaN){
	if(value >= limit){
	  localStorage.setItem("ch4-counter", "Stop");
	  timesRun += 1;
	}else{
	  value = parseInt(value)+1;
	  localStorage.setItem("ch4-counter", value);
	}
	var rem = limit - value;
	var mins = Math.floor(rem/60);
	var sec = Math.floor(rem % 60);
	document.getElementById('demo').innerHTML = "Hint: " + mins +"m " + sec + "s ";
}};

var interval = setInterval(function (){counter();}, 1000);

function loadHint_Login() {
	var urlSend = "42E6AE8EA03B/show_hint.php";
	var value = localStorage.getItem("ch4-counter");
	var param = "store="+value;
	var xhttp = new XMLHttpRequest();
	xhttp.onreadystatechange = function() {
	  if (this.readyState == 4 && this.status == 200) {
		document.getElementById("claude").innerHTML =
		this.responseText;
	  }
	};
	xhttp.open("GET", urlSend+"?"+param, true);
	xhttp.send();
}